import React, { ReactNode, ReactElement } from "react";

interface WithSession {
  session: Session;
  "data-session": string;
}

interface Session {
  user?: {
    name?: string;
    email?: string;
  };
  [key: string]: any;
}

interface PublicWrapperProps {
  children: ReactNode;  // We want to process each child inside the wrapper
  session: Session;     // Session to pass down to children
}

export default function PublicWrapper({
  children,
  session,
}: PublicWrapperProps) {
  if (!children) {
    throw new Error("Invalid children: children must be a valid React element.");
  }

  // Ensure children are valid React elements
  const processChild = (child: ReactNode): ReactNode => {
    if (!React.isValidElement(child)) {
      return null;
    }

    // If child is a Fragment, process its children
    if (child.type === React.Fragment) {
      const element = child as ReactElement<{ children?: ReactNode }>;
      return React.Children.map(element.props.children, processChild);
    }

    // Clone element with session prop
    return React.cloneElement<WithSession>(
      child as ReactElement<WithSession>,
      { 
        session,
        "data-session": JSON.stringify(session) // Add session as a prop
      }
    );
  };

  const processedChildren = React.Children.map(children, processChild);

  // Return processed children, wrapping in Fragment if there are multiple children
  return processedChildren?.length === 1 
    ? processedChildren[0] 
    : React.createElement(React.Fragment, null, processedChildren);
}
